import "./index.css";
const message = ["First", "Second", "Third"];
function App() {
  const step = 2;
  return (
    <div className="steps">
      <div className="numbers">
        <div> 1</div>
        <div>2</div>
        <div>3</div>
      </div>
      <p className="message">
        Step {step}: {message[step - 1]}
      </p>
      <div className="buttons">
        <button style={{ backgroundColor: " #7950f2", color: "#fff" }}>
          Previous
        </button>
        <button style={{ backgroundColor: " #7950f2", color: "#fff" }}>
          Next
        </button>
      </div>
    </div>
  );
}
export default App;
